import Boom from 'boom';

// Usage:
const notFound = Boom.notFound('Resource not found');
const badRequest = Boom.badRequest('Invalid input');
const unauthorized = Boom.unauthorized('Must authenticate', 'Bearer');
return { 
  success: notFound.output.statusCode === 404 && badRequest.output.statusCode === 400 && unauthorized.output.statusCode === 401, 
  result: { notFound: notFound.output.statusCode, badRequest: badRequest.output.statusCode, unauthorized: unauthorized.output.statusCode } 
};
