import debounce from 'debounce';

// Usage:
let callCount = 0;
const debouncedFn = debounce(() => { callCount++; }, 100);
debouncedFn();
debouncedFn();
debouncedFn();
// Wait for debounce delay
await new Promise(resolve => setTimeout(resolve, 150));
return { success: callCount === 1, result: callCount };
